<script type="text/javascript">
  // 判断是否为移动端运行环境 
  if (/AppleWebKit.*Mobile/i.test(navigator.userAgent) || (
      /MIDP|SymbianOS|NOKIA|SAMSUNG|LG|NEC|TCL|Alcatel|BIRD|DBTEL|Dopod|PHILIPS|HAIER|LENOVO|MOT-|Nokia|SonyEricsson|SIE-|Amoi|ZTE/
