    // 如果以上都不是，则加载以下样式 
    setActiveStyleSheet("style_mobile_no.css");
  }

  // 判断完毕后加载样式 
  function setActiveStyleSheet(filename) {
    document.write("<link href=" + filename + " rel=stylesheet>");
  }
